mapper.select:
select *
from artist
where artist_name like #{search}
   or hashtag like #{search}

select *
from artist
where artist_name like #{search}
   or hashtag like #{search}
LIMIT 15

select *
from artist
WHERE artist_no < #{artist_no}
  AND recent_act_date <= #{recent_act_date}
order by recent_act_date DESC, artist_no DESC
limit 10

select *
from artist
WHERE artist_no < #{artist_no}
  AND artist_name > #{artist_name}
order by artist_name, artist_no DESC
limit 10

select *
from artist
WHERE artist_no < #{artist_no}
  AND fan_number <= #{fan_number}
order by fan_number DESC, artist_no DESC
limit 10

mapper.update:
update artist
SET loudsourcing_push=#{loudsourcing_push}
WHERE artist_no = #{artist_no}

