mapper.select:
select *
from loudsourcing
WHERE loudsourcing_no = #{loudsourcing_no}

select *
from loudsourcing
WHERE name = #{query}
   OR host = #{query}
   OR hashtag = #{hashtag}

select *
from loudsourcing
WHERE name LIKE #{query}
   OR host LIKE #{query}
   OR hashtag LIKE #{query}
ORDER BY revise_date DESC, loudsourcing_no DESC
LIMIT 10
