mapper.select:
select *
from loudsourcingapply
WHERE artist_no = #{artist_no}
  AND loudsourcing_no = #{loudsourcing_no}

mapper.delete:
delete
from loudsourcingapply
WHERE artist_no = #{artist_no}
  AND loudsourcing_no = #{loudsourcing_no}

mapper.update:
update loudsourcingapply
SET entry=#{entry}
WHERE apply_no = #{apply_no}

update loudsourcingapply
SET is_pre_selected=#{is_pre_selected}
WHERE apply_no = #{apply_no}

update loudsourcingapply
SET is_selected=#{is_selected}
WHERE apply_no = #{apply_no}
