mapper.select:
select *
from spon
WHERE user_no = #{user_no}
  AND artist_no = #{artist_no}

mapper.update:
UPDATE spon
SET status= true,
    apply_date=#{apply_date}
WHERE spon_no = #{spon_no}

UPDATE spon
SET purchase_status= true,
    send_date=#{send_date}
WHERE spon_no = #{spon_no}

update spon
SET verify_status=#{verify_status}
WHERE spon_no = #{spon_no}
